css:
.top-bar {
    background-color: #fcce66 !important;
}

.top-bar__nav a {
    color: white !important;
}

.header {
    background-color: #fcce66;
}

.fa-user:before {
    color: white !important;
}
.fa-user:hover:before {
    color: #d0d0d0 !important;
}
.fa-shopping-bag:before {
    color: white !important;
}
.fa-shopping-bag:hover:before {
    color: #d0d0d0 !important;
}
.fa-bars {
    color: white !important;
}

.fa-search {
    color: #fcce66;
}

.header__toggler span {
    color: white;
    font-size: 80%;
}

.header__search ::placeholder {
    color: #fcce66;
    font-size: 90%;
}

.banner {
    display: flex;
    align-items: center;
    justify-content: center;
}

@media screen and (min-width: 481px) and (max-width: 991px) {
    .fa-search {
        color: white;
    }
}
@media screen and (max-width: 480px) {
    .fa-search {
        color: white;
    }
}
